body.script:
//
function toggleTheme() {
    const body = document.body;
    const icon = document.querySelector('.theme-toggle i');

    if (body.classList.contains('dark-theme')) {            body.classList.remove('dark-theme');
        body.classList.add('light-theme');
        icon.className = 'fas fa-moon';
        localStorage.setItem('theme', 'light');
    } else {
        body.classList.remove('light-theme');
        body.classList.add('dark-theme');
        icon.className = 'fas fa-sun';
        localStorage.setItem('theme', 'dark');
    }
}

window.onscroll = function() {
    if (document.body.scrollTop > 20 || document.documentElement.scrollTop > 20) {
        document.getElementById('return-to-top').style.display = 'flex';
    } else {
        document.getElementById('return-to-top').style.display = 'none';
    }
};

function scrollToTop() {
    window.scrollTo({
        top: 0,
        behavior: 'smooth'
    });
}

async function fetchRates() {
    const url = 'https://docs.google.com/spreadsheets/d/e/2PACX-1vSNDdKNRmuS_lu66UUjPilT7lUNXogFk3ByljcyJHDRIUoPh5Lk_PCQ0dp7I5Td-YL55KWe1_WCeku5/pub?output=csv&gid=0';

    try {
        const controller = new AbortController();
        const timeoutId = setTimeout(() => controller.abort(), 5000);

        const response = await fetch(url, { signal: controller.signal });
        clearTimeout(timeoutId);

        const data = await response.text();
        const rows = data.split('\n').map(row => row.split(','));

        const thead = document.createElement('thead');
        const tbody = document.createElement('tbody');
        const table = document.createElement('table');

        const headerRow = document.createElement('tr');
        rows[0].forEach(header => {
            const th = document.createElement('th');
            th.textContent = header;
            headerRow.appendChild(th);
        });
        thead.appendChild(headerRow);

        for (let i = 1; i < rows.length; i++) {
            const tr = document.createElement('tr');
            rows[i].forEach((cell, index) => {
                const td = document.createElement('td');
                td.textContent = index > 0 && !isNaN(cell.trim()) ? `${cell.trim()} Ks` : cell;
                tr.appendChild(td);
            });
            tbody.appendChild(tr);
        }

        table.appendChild(thead);
        table.appendChild(tbody);

        requestAnimationFrame(() => {
            const rateTable = document.getElementById('rateTable');
            rateTable.innerHTML = '';
            rateTable.appendChild(table);
            updateTime();
        });

    } catch (error) {
        if (error.name === 'AbortError') {
            document.getElementById('rateTable').innerHTML = '<div class="error">Loading timeout. Please refresh.</div>';
        } else {
            console.error('Error:', error);
            document.getElementById('rateTable').innerHTML = '<div class="error">Failed to load rates.</div>';
        }
    }
}

function updateTime() {
    const now = new Date();
    const timeString = now.toLocaleTimeString('en-US', {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true
    });
    const dateString = now.toLocaleDateString('en-GB', {
        day: '2-digit',
        month: '2-digit',
        year: '2-digit'
    });
    document.getElementById('updateTime').textContent = 
        `Exchange Rate Update on: ${timeString}, ${dateString}`;
}

async function showFeeTable(bank) {
    const button = document.querySelector(`button[onclick="showFeeTable('${bank}')"]`);
    const existingTable = document.getElementById(`${bank}-table`);

    // Close other tables
    document.querySelectorAll('.fee-button').forEach(btn => {
        if (btn !== button) {
            btn.classList.remove('active');
            const otherTable = document.getElementById(btn.onclick.toString().match(/"([^"]+)"/)[1] + '-table');
            if (otherTable) {
                otherTable.style.maxHeight = '0';
                otherTable.style.opacity = '0';
                setTimeout(() => otherTable.remove(), 300);
            }
        }
    });

    // Toggle current button
    button.classList.toggle('active');

    // If table exists, remove it
    if (existingTable) {
        existingTable.style.maxHeight = '0';
        existingTable.style.opacity = '0';
        setTimeout(() => existingTable.remove(), 300);
        return;
    }

    const urls = {
        uab: 'https://docs.google.com/spreadsheets/d/e/2PACX-1vSNDdKNRmuS_lu66UUjPilT7lUNXogFk3ByljcyJHDRIUoPh5Lk_PCQ0dp7I5Td-YL55KWe1_WCeku5/pub?gid=245625530&single=true&output=csv',
        aya: 'https://docs.google.com/spreadsheets/d/e/2PACX-1vSNDdKNRmuS_lu66UUjPilT7lUNXogFk3ByljcyJHDRIUoPh5Lk_PCQ0dp7I5Td-YL55KWe1_WCeku5/pub?gid=1640510518&single=true&output=csv',
        cb: 'https://docs.google.com/spreadsheets/d/e/2PACX-1vSNDdKNRmuS_lu66UUjPilT7lUNXogFk3ByljcyJHDRIUoPh5Lk_PCQ0dp7I5Td-YL55KWe1_WCeku5/pub?gid=605862732&single=true&output=csv',
        kbz: 'https://docs.google.com/spreadsheets/d/e/2PACX-1vSNDdKNRmuS_lu66UUjPilT7lUNXogFk3ByljcyJHDRIUoPh5Lk_PCQ0dp7I5Td-YL55KWe1_WCeku5/pub?gid=1744659778&single=true&output=csv',
        mab: 'https://docs.google.com/spreadsheets/d/e/2PACX-1vSNDdKNRmuS_lu66UUjPilT7lUNXogFk3ByljcyJHDRIUoPh5Lk_PCQ0dp7I5Td-YL55KWe1_WCeku5/pub?gid=1796926669&single=true&output=csv'
    };

    try {
        // Create container
        const tableContainer = document.createElement('div');
        tableContainer.id = `${bank}-table`;
        tableContainer.className = 'fee-table-container';
        tableContainer.innerHTML = '<div class="loading">Loading fees...</div>';

        // Insert after button
        button.insertAdjacentElement('afterend', tableContainer);

        // Fetch data
        const response = await fetch(urls[bank]);
        const data = await response.text();
        const rows = data.split('\n').map(row => row.split(','));

        // Create table
        const table = document.createElement('table');
        table.className = 'fee-table';

        // Add header
        const thead = document.createElement('thead');
        const headerRow = document.createElement('tr');
        rows[0].forEach(header => {
            const th = document.createElement('th');
            th.textContent = header.trim();
            headerRow.appendChild(th);
        });
        thead.appendChild(headerRow);
        table.appendChild(thead);

        // Add body
        const tbody = document.createElement('tbody');
        for (let i = 1; i < rows.length; i++) {
            const tr = document.createElement('tr');
            rows[i].forEach((cell, index) => {
                const td = document.createElement('td');
                td.textContent = index > 0 && !isNaN(cell.trim()) ? 
                    `${cell.trim()} Ks` : cell.trim();
                tr.appendChild(td);
            });
            tbody.appendChild(tr);
        }
        table.appendChild(tbody);

        // Replace loading with table
        tableContainer.innerHTML = '';
        tableContainer.appendChild(table);

        // Trigger animation
        requestAnimationFrame(() => {
            tableContainer.style.maxHeight = '800px';
            tableContainer.style.opacity = '1';
        });
    } catch (error) {
        console.error('Error:', error);
        button.classList.remove('active');
        document.getElementById(`${bank}-table`)?.remove();
    }
}

document.addEventListener('DOMContentLoaded', function() {
    const savedTheme = localStorage.getItem('theme') || 'dark';
    const body = document.body;
    const icon = document.querySelector('.theme-toggle i');

    if (savedTheme === 'light') {
        body.classList.remove('dark-theme');
        body.classList.add('light-theme');
        icon.className = 'fas fa-moon';
    }

    fetchRates();
    setInterval(fetchRates, 60000);
});
